function getSubtotalPercent(t,e){var a=t*(e/100),n=Math.round(100*a)/100;return n}function calculateTotal(){var t;t=Number(subTotal.value);var e=document.getElementsByTagName("option")[tipRate.selectedIndex].value,a=getSubtotalPercent(t,e);totalTip.innerHTML=a,totalResult.innerHTML=Math.round(100*(t+a))/100}var subTotal=document.getElementById("subTotalInput"),totalTip=document.getElementById("totalTip"),totalResult=document.getElementById("totalResult"),tipRate=document.getElementById("tipRates");subTotal.onkeyup=function(){calculateTotal()},tipRate.onchange=function(){calculateTotal()};